import React from "react";
import "./BusBookingPage.css";

const BusBookingPage = () => {
  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h1 className="card-title">Bus Booking Information</h1>
          <p className="card-subtitle">
            View and manage your bus booking details below.
          </p>
        </div>
        <div className="card-content">
          <ul className="booking-list">
            <BookingDetail icon="bus" label="Bus Number" value="BUS001" />
            <BookingDetail
              icon="map-pin"
              label="Route"
              value={
                <>
                  <span className="tag">New York</span>
                  <span className="tag">Washington D.C.</span>
                </>
              }
            />
            <BookingDetail
              icon="clock"
              label="Departure Time"
              value="09:00 AM, June 15, 2023"
            />
            <BookingDetail
              icon="clock"
              label="Arrival Time"
              value="02:00 PM, June 15, 2023"
            />
            <BookingDetail
              icon="map-pin"
              label="Seat Number"
              value="A23 (Window Seat)"
            />
          </ul>
        </div>
        <div className="card-footer">
          <button className="edit-button">
            <span className="edit-icon">‚úèÔ∏è</span>
            Edit Booking
          </button>
        </div>
      </div>
    </div>
  );
};

const BookingDetail = ({ icon, label, value }) => (
  <li className="booking-item">
    <div className="booking-label">
      <span className="booking-icon">{getIcon(icon)}</span>
      <span className="booking-label-text">{label}</span>
    </div>
    <div className="booking-value">{value}</div>
  </li>
);

const getIcon = (iconName) => {
  switch (iconName) {
    case "bus":
      return "üöå";
    case "map-pin":
      return "üìç";
    case "clock":
      return "üïí";
    default:
      return "‚ÑπÔ∏è";
  }
};

export default BusBookingPage;
