import React, { useState } from "react";
import "./BusBookingForm.css";

const BusBookingForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    contactNo: "",
    email: "",
    boardingPoint: "Kathmandu (07:30 PM)",
    dropPoint: "Lumbini",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log(formData);
  };

  return (
    <div className="booking-container">
      <h1 className="form-title">Passenger Details</h1>
      <form className="booking-form" onSubmit={handleSubmit}>
        <label>
          Name
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Contact No
          <input
            type="tel"
            name="contactNo"
            value={formData.contactNo}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Email (Ticket will be sent on Email)
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Boarding Point
          <select
            name="boardingPoint"
            value={formData.boardingPoint}
            onChange={handleChange}
          >
            <option value="Kathmandu (07:30 PM)">Kathmandu (07:30 PM)</option>
            {/* Add more options as needed */}
          </select>
        </label>
        <label>
          Drop Point
          <select
            name="dropPoint"
            value={formData.dropPoint}
            onChange={handleChange}
          >
            <option value="Lumbini">Lumbini</option>
            {/* Add more options as needed */}
          </select>
        </label>
        <button type="submit" className="submit-button">
          NEXT
        </button>
      </form>
    </div>
  );
};

export default BusBookingForm;
