import React, { useState } from "react";
import "./BusBookingForm.css";

const BusBookingForm = () => {
  const [formData, setFormData] = useState({
    from: "New York",
    to: "Washington D.C.",
    departureTime: "10:00 AM",
    busNumber: "BX123"
  });

  const handleEdit = () => {
    // Handle edit logic here
    console.log("Edit booking:", formData);
  };

  return (
    <div className="container">
      <div className="bus-booking-form">
        <div className="form-header">
          <h2>Bus Booking Information</h2>
          <p>View and manage your bus booking details below.</p>
        </div>
        <div className="form-content">
          <div className="form-row">
            <span className="label">
              <span className="icon">🔴</span> From
            </span>
            <span className="value">{formData.from}</span>
          </div>
          <div className="form-row">
            <span className="label">
              <span className="icon">🔴</span> To
            </span>
            <span className="value">{formData.to}</span>
          </div>
          <div className="form-row">
            <span className="label">
              <span className="icon">🕒</span> Departure Time
            </span>
            <span className="value">{formData.departureTime}</span>
          </div>
          <div className="form-row">
            <span className="label">
              <span className="icon">🚌</span> Bus Number
            </span>
            <span className="value">{formData.busNumber}</span>
          </div>
        </div>
        <button className="edit-button" onClick={handleEdit}>
          <span className="icon">✏️</span> Edit Booking
        </button>
      </div>
    </div>
  );
};

export default BusBookingForm;
